soup = BeautifulSoup(html_content, "html.parser")

codelists = list()
for link in soup.find_all("a"):
    if link.get("href").startswith("/edifact/d02b/cl"):
        codelists.append(link.get("href"))
codelists = list(set(codelists))

cl_json = dict()

for codelist in codelists:
    cl_html_file = f"{codelist.split('/')[3].replace('cl', '')}.html"

    with open(cl_html_file, "r") as html_file:
        cl_html_content = html_file.read()

    cl_soup = BeautifulSoup(cl_html_content, "html.parser")

    cl_name = cl_soup.find("div", attrs={"class": "titles"}).find("h1").text.split(" ")[-1]
    cl_data = f"codelists/codelist_{cl_name}.json"
    cl_desc = cl_soup.find("h2").text

    cl_json[cl_name] = {
        "data": cl_data,
        "desc": cl_desc
    }

    cl_file = f"../codelists/codelist_{cl_name}.json"
    description = cl_soup.find("h2")
    cl_codelist = dict()

    for item in cl_soup.find_all("tr"):
        cd = item.find("td", attrs={"class": "cd"}).text
        name = item.find("div", attrs={"class": "name"}).text
        desc = item.find("div", attrs={"class": "desc"}).text

        cl_codelist[cd] = {
            "name": name,
            "desc": desc
        }

    with open(cl_file, "w") as json_file:
        json.dump(cl_codelist, json_file, indent=4, sort_keys=False)
    print(f"{cl_file} created successfully")

with open("codelists.json", "w") as json_file:
    json.dump(cl_json, json_file, indent=4, sort_keys=True)
print(f"{json_file.name} created successfully")
